import React, { useState, useEffect } from "react";
import "./styling/App.css";
import Post from "./components/post";
import Axios from "axios";
import NewForm from "./components/newForm";

function App() {
  const [posts, setPosts] = useState([]);

  // Providing an empty array ensures that this request only runs once
  useEffect(() => {
    async function getData() {
      const response = await Axios.get("api/posts/posts");
      setPosts(response.data);
    }
    getData();
  }, []);

  return (
    <div className="container">
      <p>
        <a href="/api/posts">&laquo; Back to public homepage</a>
      </p>
      <NewForm setPosts={setPosts} />
      <div className="post-grid">
        {posts.map(function (post) {
          return <Post title={post.title} topic={post.topic} />;
        })}
      </div>
    </div>
  );
}

export default App;
